import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { lastValueFrom } from 'rxjs/internal/lastValueFrom';
import { Question } from './types';

@Injectable({
  providedIn: 'root'
})
export class QuizService {

  private name: string = "";
  private questions: Question[] = [];

  constructor(private _http: HttpClient) {
    this.loadQuestions();
  }

  get Name(): string {
    return this.name;
  }

  async loadQuestions() {
    let questions = await lastValueFrom(this._http.get<Question[]>("http://localhost:3000/pokemon"));
    this.questions = questions;
  }

  getQuestionsByDifficulty(difficulty: string, amount: number) {
    let qByDiff:Question[] = this.questions.filter((q) => q.difficulty == difficulty);
