import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { lastValueFrom } from 'rxjs/internal/lastValueFrom';
import { Question } from './types';

@Injectable({
  providedIn: 'root'
})
export class QuizService {

  private name: string = "";
  private questionsList: Question[] = [];

  constructor(private _http: HttpClient) {
    this.loadQuestions();
  }

  get Name(): string {
    return this.name;
  }

  async loadQuestions() {
    let questions = await getQuestionsFromAPI();
    this.questionsList = questions;
  }

  async ophalen() {
    let pokedex = await this.GetNiceDataFromApi();
    for (let result of pokedex) {
      result.favorite = false;
      this.pokemonList.push(result);
    }
  }

  public getQuestionsFromAPI() : Promise<Question[]> {
    return lastValueFrom(this._http.get<Question[]>("http://localhost:3000/question");
  }

